mapper.select:
select

from t_credentials_info
where Fuid = #{uid,jdbcType=BIGINT}
and Fcard_type = #{cardType,jdbcType=TINYINT}
and Fhist_seq=#{histSeq,jdbcType=INTEGER}

select

from t_credentials_info


        and Fhist_seq = #{histSeq}


        and Fuid = #{uid}


        AND Fcard_type = #{cardType}


        AND Fcard_no = #{cardNo}


        AND Fstatus = #{status}


        AND Fmain = #{main}
